package week2.day1;

public class Isprime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            int a=5;
          
    		
            for(int i=2; i<a; i++) 
			{
				if (a%i==0)
				{
				
					System.out.println(a + " is a not a Prime Number");
					 
				}
				else {
					System.out.println(a + " is a Prime Number");
				}
			}
            }
	}


